import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

export default function AddProduct() {
  const navigate = useNavigate();

  const [productinfo, setProductinfo] = useState({
    productName: "",
    productDescription: "",
    pricing: "",
    specification: "",
    availability: "",
  });

  const {
    productName,
    productDescription,
    pricing,
    specification,
    availability,
  } = productinfo;

  const onInputChange = (e) => {
    setProductinfo({ ...productinfo, [e.target.productName]: e.target.value });
  };

  const onAdd = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:8080/product", productinfo);
    navigate("/");
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
          <h2 className="text-center m-4">Add Product</h2>

          <form onSubmit={onAdd}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">
                productName
              </label>
              <input
                type="text"
                className="form-control"
                id="productName"
                name="productName"
                value={productName}
                onChange={onInputChange}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="productDescription" className="form-label">
                productDescription
              </label>
              <input
                type="text"
                className="form-control"
                id="productDescription"
                name="productDescription"
                value={productDescription}
                onChange={onInputChange}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="pricing" className="form-label">
                pricing
              </label>
              <input
                type="text"
                className="form-control"
                id="pricing"
                name="pricing"
                value={pricing}
                onChange={onInputChange}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="specification" className="form-label">
                Specification
              </label>
              <input
                type="text"
                className="form-control"
                id="specification"
                name="specification"
                value={specification}
                onChange={onInputChange}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="availability" className="form-label">
                Availability
              </label>
              <input
                type="text"
                className="form-control"
                id="availability"
                name="availability"
                value={availability}
                onChange={onInputChange}
              />
            </div>

            <button type="submit" className="btn btn-primary me-2">
              Add
            </button>
            <Link to="/" className="btn btn-secondary">
              Cancel
            </Link>
          </form>
        </div>
      </div>
    </div>
  );
}
