import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./AddProduct.css";

const AddProduct = () => {
  const navigate = useNavigate();

  const [productInfo, setProductInfo] = useState({
    productName: "",
    productDescription: "",
    category: "",
    price: "",
    brand: "",
    color: "",
  });

  const { productName, productDescription, category, price, brand, color } =
    productInfo;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProductInfo({ ...productInfo, [name]: value });
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:8080/product", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(productInfo),
      });

      if (response.ok) {
        navigate("/product");
      } else {
        console.error("Failed to add product:", response.statusText);
      }
    } catch (error) {
      console.error("Error adding product:", error);
    }
  };

  return (
    <div className="container">
      <h2 className="add-head">AddProduct</h2>
      <div className="form-container">
        <form id="register" onSubmit={handleAddProduct}>
          <div>
            <label htmlFor="productName">Product Name</label>
            <br />
            <input
              type="text"
              className="form-input"
              id="productName"
              name="productName"
              value={productName}
              onChange={handleInputChange}
            />
          </div>
          <br />

          <div>
            <label htmlFor="productDescription">Product Description</label>
            <br />
            <textarea
              type="text"
              className="form-input-desc"
              id="productDescription"
              name="productDescription"
              value={productDescription}
              onChange={handleInputChange}
            ></textarea>
          </div>
          <br />

          <div>
            <label htmlFor="category">Category</label>
            <br />
            <input
              type="text"
              className="form-input"
              id="category"
              name="category"
              value={category}
              onChange={handleInputChange}
            />
          </div>
          <br />

          <div>
            <label htmlFor="price">Price</label>
            <br />
            <input
              type="text"
              className="form-input"
              id="price"
              name="price"
              value={price}
              onChange={handleInputChange}
            />
          </div>
          <br />

          <div>
            <label htmlFor="brand">Brand</label>
            <br />
            <input
              type="text"
              className="form-input"
              id="brand"
              name="brand"
              value={brand}
              onChange={handleInputChange}
            />
          </div>
          <br />

          <div>
            <label htmlFor="color">Color</label>
            <br />
            <input
              type="text"
              className="form-input"
              id="color"
              name="color"
              value={color}
              onChange={handleInputChange}
            />
          </div>
          <br />
          <div className="btn">
            <button className="add-btn" type="submit">
              Add
            </button>
            <div className="cen-btn">
              <Link id="canle-link" to="/product">
                Cancel
              </Link>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddProduct;

