import React, { useState } from "react";

export default function AddProduct() {
  const [productinfo, setProductinfo] = useState({
    name: "",
    desc: "",
    price: "",
    spesification: "",
    availability: "",
  });
  // const [data, setData] = useState({});

  const { name, desc, price, spesification, availability } = productinfo;

  // const AddProduct = () => {
  //   setData([...data, productinfo]);
  //   setProductinfo({});

  //   setProductinfo({ data: data });
  // };

  const onInputChange = (e) => {
    setProductinfo({ ...productinfo, [e.target.name]: e.target.value });
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
          <h2 className="text-center m-4">Products</h2>

          <div className="mb-3">
            <label htmlFor="Name" className="form-lable">
              Name
            </label>
            <input
              type={"text"}
              className="form-control"
              placeholder="Product Name"
              name="name"
              value={name}
              onChange={(e) => onInputChange(e)}
              // onChange={(e) =>
              //   setProductinfo({ ...productinfo, pname: e.target.value })
              // }
            />
          </div>

          <div className="mb-3">
            <label htmlFor="Name" className="form-lable">
              Descrption
            </label>
            <input
              type={"text"}
              className="form-control"
              placeholder="Product Descrption"
              name="descrption"
              value={desc}
              onChange={(e) => onInputChange(e)}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="Name" className="form-lable">
              Price
            </label>
            <input
              type={"text"}
              className="form-control"
              placeholder="In dolars"
              name="price"
              value={price}
              onChange={(e) => onInputChange(e)}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="Name" className="form-lable">
              Specification
            </label>
            <input
              type={"text"}
              className="form-control"
              placeholder="size of the cloth"
              name="specification"
              value={spesification}
              onChange={(e) => onInputChange(e)}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="Name" className="form-lable">
              Availability
            </label>
            <input
              type={"text"}
              className="form-control"
              placeholder="Availability"
              name="availability"
              value={availability}
              onChange={(e) => onInputChange(e)}
            />
          </div>
          <button
            type="submit"
            className="btn btn-outline-primary"
            onClick={AddProduct}
          >
            ADD
          </button>
          <button type="submit" className="btn btn-outline-danger mx-2">
            clear
          </button>
        </div>
      </div>
    </div>
  );
}
